import styled from 'styled-components'

export const ModalInput = styled.input`
  border-radius: 4px;
  width: 90%;
  height: 2rem;
  margin: 0rem 1rem;
  margin-bottom: 1rem;
  border: 1px solid black;
  outline: none;

  &:focus {
    border: 2.5px solid #2c51b8;
  }
`
